dotPicWidth       equ 15
dotPicHeight      equ 15
dotJump           equ 33

dotOffset         dw 0
optionSelected    db 1

upArrowScanCode   equ 72
downArrowScanCode equ 80
enterScanCode     equ 28

gameText          db "This is the game", 13, 10, '$'
helpText          db "Get help here!", 13, 10, '$'
aboutText         db "Made by Almog Hamdani!", 13, 10, '$'

		CODESEG
Start:
        ; Set data segment
        mov ax, @data
        mov ds, ax

        ; Set video memory as extra segment
        mov ax, 0A000h
        mov es, ax

        ; Set graphic mode
        mov ax, 13h
        int 10h

PrintOpeningScreen:
        CopyString OpeningFileName, FileName, OpeningNameLen
        mov [StartX], 0
        mov [StartY], 0

        call DrawPCX

        ; Wait for input
        xor ah, ah
        int 16h

PrintMenu:
        ClearScreen

        ; Print title
        mov [Y], 0
        call PrintTitle

        ; Set menu position on screen
        mov [X], (320 / 2) - (menuPicWidth / 2) ; Set menu in middle
        mov [Y], (200 / 2) - (menuPicHeight / 2) + 25 ; Set menu in middle and a little bit down

        ; Set menu size
        mov dx, menuPicWidth ; Width
        mov cx, menuPicHeight ; Height

        lea si, [menuPic] ; Set menu offset

        ; Print menu
        call PrintSprite

        xor cx, cx ; Prepare for dot print

PrintSelectDot:
        ; Set dot' position
        mov [X], (320 / 2) - (menuPicWidth / 2) - 15 ; Menu position minus 15
        mov [Y], (200 / 2) - (menuPicHeight / 2) + 25 + 8 ; Menu position plus 8

        mov ax, [dotOffset]
        add [Y], ax

        ; Set dot' size
        mov cx, dotPicHeight
        mov dx, dotPicWidth

        lea si, [dotPic] ; Set dot pic offset

        call PrintSprite ; Print dot

CheckArrows:
        ; Get input from keyboard
        xor ah, ah
        int 16h

        cmp ah, enterScanCode ; Check if enter is pressed
        je JumpToSelected

        cmp ah, upArrowScanCode ; Check if up arrow was pressed
        je HandleUp

        cmp ah, downArrowScanCode ; Check if down arrow was pressed
        je HandleDown

        jmp CheckArrows

HandleUp:
        ; If current option is the first, don't go up
        cmp [optionSelected], 1
        je CheckArrows

        ; Set dot' position
        mov [X], (320 / 2) - (menuPicWidth / 2) - 15 ; Menu position minus 15
        mov [Y], (200 / 2) - (menuPicHeight / 2) + 25 + 8 ; Menu position plus 8

        mov ax, [dotOffset]
        add [Y], ax

        ; Set dot' size
        mov cx, dotPicHeight
        mov dx, dotPicWidth

        call ClearSprite

        dec [optionSelected] ; Set option to be the previous
        sub [dotOffset], dotJump ; Set currect offset

        jmp PrintSelectDot ; Print dot again

HandleDown:
        ; If current option is the last, don't go down
        cmp [optionSelected], 4
        je CheckArrows

        ; Set dot' position
        mov [X], (320 / 2) - (menuPicWidth / 2) - 15 ; Menu position minus 15
        mov [Y], (200 / 2) - (menuPicHeight / 2) + 25 + 8 ; Menu position plus 8

        mov ax, [dotOffset]
        add [Y], ax

        ; Set dot' size
        mov cx, dotPicHeight
        mov dx, dotPicWidth

        call ClearSprite

        inc [optionSelected] ; Set option to be the next
        add [dotOffset], dotJump ; Set currect offset

        jmp PrintSelectDot ; Print dot again

JumpToSelected:
        ; Jump to the selected option

        ; Set text mode
        mov ax, 3h
        int 10h

        cmp [optionSelected], 1
        je StartGame

        cmp [optionSelected], 2
        je ShowHelp

        cmp [optionSelected], 3
        je ShowAbout

        jmp Exit

StartGame:

        call Game

ShowHelp:

        call Help

ShowAbout:

        call About

Exit:
        ; Exit
        mov ax, 4C00h
        int 21h

;------------------------
;PrintTitle - Prints the title(game' name) anywhere in the screen
;------------------------
;Input:
;       Y <- Title' Y axis
;Output:
;       None
;Registers:
;       AX, BX, CX, DX, SI, DI, ES
;------------------------
PROC PrintTitle

        ; Set title' position
        mov [X], (320 / 2) - (titlePicWidth / 2)

        ; Set title' size
        mov cx, titlePicHeight
        mov dx, titlePicWidth

        lea si, [titlePic] ; Set title pic offset

        call PrintSprite

        ret
ENDP PrintTitle

PROC Game

        ; Print text
        mov dx, offset gameText
        mov ah, 09h
        int 21h

        ; Wait for keyboard tap
        xor ah, ah
        int 16h

        jmp PrintMenu ; Return to menu

        ret
ENDP Game

PROC Help

        ; Print text
        mov dx, offset helpText
        mov ah, 09h
        int 21h

        ; Wait for keyboard tap
        xor ah, ah
        int 16h

        jmp PrintMenu ; Return to menu

        ret
ENDP Help

PROC About

        ; Print text
        mov dx, offset aboutText
        mov ah, 09h
        int 21h

        ; Wait for keyboard tap
        xor ah, ah
        int 16h

        jmp PrintMenu ; Return to menu

        ret
ENDP About

