        db 0,48,48,49,49,50,50,51,51,52,52,52,53,53,0
        db 0,48,49,49,50,50,51,51,52,52,53,53,53,54,0
        db 0,49,49,50,50,51,51,52,52,53,53,53,54,54,0
        db 0,49,50,50,51,51,52,52,53,53,53,54,54,55,0
