		CODESEG
Start:
        ; Set data segment
        push @data
        pop ds

        ; Set video memory as extra segment
        push 0A000h
        pop es

        ; Set graphic mode
        mov ax, 13h
        int 10h

PrintOpeningScreen:
        ; Print title in y axis 40
        mov bx, 30
        call PrintTitle

        ; Printing press any key and credits
        ; Set position
        mov ax, (320 / 2) - (openingScreenWidth / 2)
        mov bx, 125

        ; Set size
        mov ch, openingScreenHeight
        mov dx, openingScreenWidth

        lea si, [openingScreenPic]

        call PrintSprite

        ; Wait for input
        xor ah, ah
        int 16h

PrintMenu:
        ClearScreen

        ; Print title
        mov bx, 0
        call PrintTitle

        ; Set menu position on screen
        mov ax, (320 / 2) - (menuPicWidth / 2) ; Set menu in middle
        mov bx, (200 / 2) - (menuPicHeight / 2) + 25 ; Set menu in middle and a little bit down

        ; Set menu size
        mov dx, menuPicWidth ; Width
        mov ch, menuPicHeight ; Height

        lea si, [menuPic] ; Set menu offset

        ; Print menu
        call PrintSprite

        xor cx, cx ; Prepare for dot print

PrintSelectDot:
        ; Set dot' position
        mov ax, (320 / 2) - (menuPicWidth / 2) - 15 ; Menu position minus 15
        mov bx, (200 / 2) - (menuPicHeight / 2) + 25 + 8 ; Menu position plus 8

        add bx, [dotOffset]

        ; Set dot' size
        mov ch, dotPicHeight
        mov dx, dotPicWidth

        lea si, [dotPic] ; Set dot pic offset

        call PrintSprite ; Print dot

CheckArrows:
        ; Get input from keyboard
        xor ah, ah
        int 16h

        cmp ah, enterScanCode ; Check if enter is pressed
        je JumpToSelected

        cmp ah, upArrowScanCode ; Check if up arrow was pressed
        je HandleUp

        cmp ah, downArrowScanCode ; Check if down arrow was pressed
        je HandleDown

        jmp CheckArrows

HandleUp:
        ; If current option is the first, don't go up
        cmp [optionSelected], 1
        je CheckArrows

        ; Set dot' position
        mov ax, (320 / 2) - (menuPicWidth / 2) - 15 ; Menu position minus 15
        mov bx, (200 / 2) - (menuPicHeight / 2) + 25 + 8 ; Menu position plus 8

        add bx, [dotOffset]

        ; Set dot' size
        mov ch, dotPicHeight
        mov dx, dotPicWidth

        call ClearSprite

        dec [optionSelected] ; Set option to be the previous
        sub [dotOffset], dotJump ; Set currect offset

        jmp PrintSelectDot ; Print dot again

HandleDown:
        ; If current option is the last, don't go down
        cmp [optionSelected], 4
        je CheckArrows

        ; Set dot' position
        mov ax, (320 / 2) - (menuPicWidth / 2) - 15 ; Menu position minus 15
        mov bx, (200 / 2) - (menuPicHeight / 2) + 25 + 8 ; Menu position plus 8

        add bx, [dotOffset]

        ; Set dot' size
        mov ch, dotPicHeight
        mov dx, dotPicWidth

        call ClearSprite

        inc [optionSelected] ; Set option to be the next
        add [dotOffset], dotJump ; Set currect offset

        jmp PrintSelectDot ; Print dot again

JumpToSelected:
        ; Jump to the selected option

        ; Set text mode
        mov ax, 3h
        int 10h

        cmp [optionSelected], 1
        je StartGame

        cmp [optionSelected], 2
        je ShowHelp

        cmp [optionSelected], 3
        je ShowAbout

        jmp Exit

StartGame:

        call Game

ShowHelp:

        call Help

ShowAbout:

        call About

Exit:
        ; Exit
        mov ax, 4C00h
        int 21h

;------------------------
;PrintTitle - Prints the title(game' name) anywhere in the screen
;------------------------
;Input:
;       BX <- Title' Y axis
;Output:
;       None
;Registers:
;       AX, BX, CX, DX, SI, DI, ES
;------------------------
PROC PrintTitle

        ; Set title' position
        mov ax, (320 / 2) - (titlePicWidth / 2)

        ; Set title' size
        mov ch, titlePicHeight
        mov dx, titlePicWidth

        lea si, [titlePic] ; Set title pic offset

        call PrintSprite

        ret
ENDP PrintTitle

PROC Game

        ; Print text
        mov dx, offset gameText
        mov ah, 09h
        int 21h

        ; Wait for keyboard tap
        xor ah, ah
        int 16h

        jmp PrintMenu ; Return to menu

        ret
ENDP Game

PROC Help

        ; Print text
        mov dx, offset helpText
        mov ah, 09h
        int 21h

        ; Wait for keyboard tap
        xor ah, ah
        int 16h

        jmp PrintMenu ; Return to menu

        ret
ENDP Help

PROC About

        ; Print text
        mov dx, offset aboutText
        mov ah, 09h
        int 21h

        ; Wait for keyboard tap
        xor ah, ah
        int 16h

        jmp PrintMenu ; Return to menu

        ret
ENDP About


;------------------------
;ClearSprite - Removes sprite from screen
;------------------------
;Input:
;       AX <- X, BX <- Y, CH <- Sprite' height, DX <- Sprite' width
;Output:
;       None
;Registers:
;       AX, BX, CX, DX, SI, DI, ES
;------------------------

PROC ClearSprite
        push dx ; Save sprite' original width

@@Cycle:
        mov cl, 0h ; Set color to black
        call PutPixel
        inc ax ; Move pixel' x by 1
        dec dx ; Decrease width by 1
        jnz @@Cycle ; While we didn't reach 0 (End of line), keep on cycle

        pop dx ; Return sprite' original width
        push dx ; Save width in stack

        sub ax, dx ; Set ax to start of line again
        inc bx ; Point to a new line

        dec ch ; Decrease sprite' height
        jnz @@Cycle

        pop dx ; Clean stack
        ret
ENDP ClearSprite

;------------------------
;PrintSprite - Prints a sprite to the screen
;------------------------
;Input:
;       AX <- X, BX <- Y, CH <- Sprite' height, DX <- Sprite' width, SI <- Sprite' offset
;Output:
;       None
;Registers:
;       AX, BX, CX, DX, SI, DI, ES
;------------------------

PROC PrintSprite
        push dx ; Save sprite' original width

@@Cycle:
        mov cl, [si] ; Get pixel' color and set in cl
        call PutPixel
        inc si ; Point to next pixel' color
        inc ax ; Move pixel' x by 1
        dec dx ; Decrease width by 1
        jnz @@Cycle ; While we didn't reach 0 (End of line), keep on cycle

        pop dx ; Return sprite' original width
        push dx ; Save width in stack

        sub ax, dx ; Set ax to start of line again
        inc bx ; Point to a new line

        dec ch ; Decrease sprite' height
        jnz @@Cycle

        pop dx ; Clean stack
        ret
ENDP PrintSprite

;------------------------
;PutPixel - Write dot
;------------------------
;Input:
;       AX <- X, BX <- Y, CL <- Color
;Output:
;       None
;Registers:
;       AX, BX, CL, DI, ES
;------------------------
PROC PutPixel
        push bx
        mov di, bx
        shl di, 8
        shl bx, 6
        add di, ax
        add di, bx
        mov [es:di], cl
        pop bx
        ret
ENDP PutPixel

